import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { getQuestions } from '../actions';

class Questions extends Component {
  // constructor(props) {
  //   super(props);
  // }

  componentDidMount() {
    const { token } = this.props;
    const { questions } = this.props;
    questions(token);
  }

  render() {
    const { question } = this.props;
    console.log(question);

    return (
      <div className="container">
        {question.map((questions, index) => (
          <div key={ index }>
            <div className="box-question">
              <div className="field-category">
                <h3 data-testid="question-category">{questions.category}</h3>
              </div>
              <div className="field-question">
                <p data-testid="question-text">{questions.question}</p>
              </div>
            </div>
            <div className="box-alternatives">
              <div>
                <button type="button" data-testid="correct-answer">
                  {question.correct_answer}
                </button>
                {question.incorrect_answers.map(((iten, position) => (
                  <button
                    type="button"
                    key={ position }
                    data-testid={ `wrong-answer-${position}` }
                  >
                    {iten}
                  </button>
                )))}
              </div>
            </div>
          </div>
        )).filter((_, index) => index === 0)}
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  token: state.user.token,
  question: state.game.questions,
});

const mapDispatchToProps = (dispatch) => ({
  questions: (token) => dispatch(getQuestions(token)),
});

Questions.propTypes = {
  token: PropTypes.object,
  question: PropTypes.func,
}.isRequired;

export default connect(mapStateToProps, mapDispatchToProps)(Questions);
