import React, { Component } from 'react';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import '../css/AnswersButton.css';

class QuestionCard extends Component {
  constructor() {
    super();
    this.state = {
      correct: false,
      incorrect: false,
      clicked: false,
      indexButtonNext: 0,
    };

    this.handleClick = this.handleClick.bind(this);
    this.handleClickCorrectAnswer = this.handleClickCorrectAnswer.bind(this);
    this.nextbutton = this.nextbutton.bind(this);
    this.next = this.next.bind(this);
  }

  nextbutton() {
    const { clicked } = this.state;

    if (clicked === true) {
      return (
        <div>
          <button type="button" onClick={ this.next } data-testid="btn-next"> PRÃ“XIMO</button>
        </div>
      );
    }
  }

  next() {
    const { indexButtonNext } = this.state;
    this.setState({
      correct: false,
      incorrect: false,
      clicked: false,
      indexButtonNext: indexButtonNext + 1,
    });
  }

  handleClickCorrectAnswer() {
    this.setState({
      clicked: true,
    });

    this.handleClick();
  }

  handleClick() {
    this.setState({
      correct: 'correct-answer',
      incorrect: 'wrong-answer',
    });
  }

  render() {
    const { questions } = this.props;
    const { indexButtonNext } = this.state;
    const { category, question } = questions[indexButtonNext];
    const { correct, incorrect } = this.state;

    return (
      <div>
        QUESTION CARD
        <div data-testidclassName="question">
          <p data-testid="question-category">{ category }</p>
          <p data-testid="question-text">{ question }</p>
        </div>
        <div className="answers">
          <button
            data-testid="correct-answer"
            className={ correct }
            onClick={ this.handleClickCorrectAnswer }
            type="button"
          >
            { questions[indexButtonNext].correct_answer }
          </button>
          {questions[indexButtonNext].incorrect_answers.map((answer, index) => (
            <button
              data-testid={ `wrong-answer-${index}` }
              type="button"
              key={ index }
              className={ incorrect }
              onClick={ this.handleClick }
            >
              { answer }
            </button>
          ))}
        </div>
        { this.nextbutton() }
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  questions: state.game.questions.results,
});

QuestionCard.propTypes = {
  questions: PropTypes.object,
}.isRequired;

export default connect(mapStateToProps, null)(QuestionCard);
