import React, { Component } from 'react';
import { connect } from 'react-redux';
import md5 from 'crypto-js/md5';
import PropTypes from 'prop-types';
import { sendScore } from '../actions';

class HeaderFeedback extends Component {
  constructor(props) {
    super(props);
    this.state = {
      score: 0,
    };
  }

  componentDidMount() {
    const { dispatchScore } = this.props;
    const { score } = this.state;
    dispatchScore(score);
  }

  render() {
    const { email, name, score } = this.props;
    const { timer } = this.state;
    const hash = md5(email).toString();
    return (
      <div>
        <img
          data-testid="header-profile-picture"
          src={ `https://www.gravatar.com/avatar/${hash}` }
          alt="imgProfile"
        />
        <p data-testid="header-player-name">
          Name:
          {name}
        </p>
        <p>
          Timer:
          {timer}
        </p>
        <span data-testid="header-score">
          Score:
          {score}
        </span>
      </div>
    );
  }
}

const mapDispatchToProps = (dispatch) => ({
  dispatchScore: (score) => dispatch(sendScore(score)),
});

const mapStateToProps = (state) => ({
  email: state.user.email,
  name: state.user.name,
  score: state.user.score,
});

HeaderFeedback.propTypes = {
  email: PropTypes.object,
  name: PropTypes.object,
  score: PropTypes.object,
  dispatchScore: PropTypes.func,
}.isRequired;

export default connect(mapStateToProps, mapDispatchToProps)(HeaderFeedback);
